const tab=document.querySelector(".tab_list"),btn_group=document.querySelectorAll(".tab_list button"),festival=document.querySelectorAll(".festival_group"),tab_select=(tab.addEventListener("click",t=>{if("BUTTON"===t.target.nodeName){const e=t.target.textContent;btn_group.forEach(t=>{t.textContent===e?t.classList.add("active"):t.classList.remove("active")}),festival.forEach(t=>{t.dataset.id===e?t.style.display="block":t.style.display="none"})}}),document.querySelector(".select_title")),now_area=document.querySelector(".area_selection"),tab_option=document.querySelector(".option"),tab_span=document.querySelectorAll(".option span"),arrow=document.querySelector(".select_title .arrow");tab_select.onclick=function(){"block"===tab_option.style.display?(tab_option.style.display="none",arrow.style.transform="rotate(0deg)"):(tab_option.style.display="block",arrow.style.transform="rotate(90deg)")},tab_option.addEventListener("click",e=>{let o=e.target.textContent;now_area.textContent=""+o,tab_span.forEach(t=>{e.target===t?e.target.style.display="none":t.style.display="block"}),festival.forEach(t=>{t.dataset.id===o?t.style.display="block":t.style.display="none"})});