const faqTitle=document.querySelectorAll(".faq_block_group li"),faqAnswer=document.querySelectorAll(".faq_answer_wrap"),innerAnswer=document.querySelectorAll(".faq_answer"),rightArrow=document.querySelectorAll(".faq_question i");function slideAnswer(e){const r=e.target.dataset.id;innerAnswer.forEach(e=>{if(e.dataset.id===r){let t=e.clientHeight;rightArrow.forEach(e=>{e.dataset.id===r?e.classList.toggle("rotate"):e.classList.remove("rotate")}),faqAnswer.forEach(e=>{e.dataset.id===r&&0===e.clientHeight?e.style.paddingBottom=t+"px":e.style.paddingBottom="0px"})}})}faqTitle.forEach(e=>{e.addEventListener("click",slideAnswer)});